import React, { useState, useEffect } from "react";
import Routes from "./Routes";
import { BrowserRouter as Router } from "react-router-dom";
import AuthApi from "./utils/createContext";
import { hasSignned, signin } from "./api/auth-api";
import axios from "axios";

function App() {
  const [auth, setAuth] = useState(false);
  const [email, setEmail] = useState();
  const [password, setPassword] = useState();
  const [userId, setUserId] = useState();

  const handleOnChange = (e) => {
    if (e.target.name === "email") {
      setEmail(e.target.value);
    } else {
      setPassword(e.target.value);
    }
  };

  const handleSignIn = async (e) => {
    e.preventDefault();
    const res = await signin({ email, password });
    if (res.data.auth) {
      setAuth(true);
    }
    //console.log(res);
    const result = await axios.post(`/auth/getuser/${email}`);
    console.log(result);
    // console.log(result.data.user[0]._id);
    const { data } = result;
    const { user } = data;
    setUserId(user[0]._id);
  };

  const readSession = async () => {
    const result = await hasSignned();
    //result.data.auth
    const { data } = result;
    const { auth } = data;
    if (auth) {
      setAuth(true);
    }
    console.log(result);
  };

  useEffect(() => {
    readSession();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <AuthApi.Provider
      value={{ auth, setAuth, handleOnChange, handleSignIn, email, password }}
    >
      <Router>
        <Routes />
      </Router>
    </AuthApi.Provider>
  );
}

export default App;
